SELECT CERT.OID_CERTIFICADO, CERT.COD_CERTIFICADO, CERT.COD_EXTERNO, CERT.COD_ESTADO, CERT.FYH_CERTIFICADO, CLIE.OID_CLIENTE, CLIE.COD_CLIENTE, CLIE.DES_CLIENTE, CERT.BOL_TODOS_SECTORES, CERT.BOL_TODOS_CANALES, CERT.BOL_TODAS_DELEGACIONES
FROM SAPR_TCERTIFICADO CERT INNER JOIN SAPR_TCONFIG_NIVEL_SALDO CONS ON CERT.OID_CONFIG_NIVEL_SALDO = CONS.OID_CONFIG_NIVEL_SALDO INNER JOIN GEPR_TCLIENTE CLIE ON CONS.OID_CLIENTE = CLIE.OID_CLIENTE
WHERE {0} = []VALOR